export function Gallery() {
    let location = useLocation();

    return (
      <div>
        <UserGrid/>
        <PhotoGrid>
          {Posts.map(i => (
            <Link
              key={i.id}
              to={{
                pathname: `/img/${i.id}`,
                // This is the trick! This link sets
                // the `background` in location state.
                state: { background: location }
              }}
            >
              <Image index={i.id} />
            </Link>
          ))}
        </PhotoGrid>
      </div>
    );
  }
